import React , {useEffect,useState} from 'react';
import Switch from '@material-ui/core/Switch';
import { useCookies } from 'react-cookie';
import {Link} from "react-router-dom";





const Nav=()=>{
    
    const [cookies, setCookie, removeCookie] = useCookies(['cookie-name']);
    const[initialStyle,updatedStyle]=useState(
      {
        color:"white"
      }
    );
    const[initialStyleNav,updatedStyleNav]=useState(
      {
        backgrountColor:"transparent",
        height:"80px",
        zIndex:1
      }
    );

            const [state, setState] = React.useState({
          checkedA: true
        });
      


        const handleChange = (event) => {
        setState({ ...state, [event.target.name]: event.target.checked });

        };           

        // state.checkedA==false?alert("hello"):alert('hi');        

            window.onscroll=()=>{
              if(window.scrollY>20){
                updatedStyle({...initialStyle,color:"black"})
                updatedStyleNav({...initialStyleNav,backgroundColor:"white"})
              }else{
                updatedStyle({...initialStyle,color:"white"})
              }
            }

    return(
        <>
         <nav className="navbar navbar-expand-md fixed-top navigation " style={{}} >
  <a className="navbar-brand" href="#" style={initialStyle}>Perfect Legal</a>
  <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#collapsibleNavbar">
    <span className="navbar-toggler-icon"></span>
  </button>
  <div className="collapse navbar-collapse" id="collapsibleNavbar">
    <ul className="navbar-nav ml-auto">
      <li className="nav-item mr-5">
        <Link  style={initialStyle} className="nav-link" to="/"> { cookies.jai&&Array.isArray(cookies.jai) ? cookies.jai.map((val)=>{return(<>{val}</>)})  : <>home</> }</Link>
      </li>
      <li className="nav-item mr-5">
        <Link  style={initialStyle} className="nav-link" to="/cookie">cookie </Link>
      </li>
      <li className="nav-item mr-5">
        <Link  style={initialStyle} className="nav-link" to="/csr">CSR</Link>
      </li> 
      <li className="nav-item dropdown mr-5">
      <Link  style={initialStyle} className="nav-link dropdown-toggle" to="#" id="navbardrop" data-toggle="dropdown">
      CONTACT US
      </Link>
      <div className="dropdown-menu">
        <Link  style={initialStyle} className="dropdown-item" to="/local">Write To Us</Link>
      </div>
    </li>
    <li className="nav-item mr-5">
        <Link  style={initialStyle} className="nav-link" to="#">QUERIES</Link>
      </li>
      <li className="nav-item mr-5">
      <Switch
        onChange={handleChange}
        name="checkedA"
        inputProps={{ 'aria-label': 'secondary checkbox' }}
      />
      </li>    
    </ul>
  </div>  
</nav>




        </>
    );
}

export default Nav;
