import React , {useEffect,useState} from 'react';
import Switch from '@material-ui/core/Switch';
import { useCookies } from 'react-cookie';
import {Link} from "react-router-dom";





const Nav=()=>{
    
    const [cookies, setCookie, removeCookie] = useCookies(['cookie-name']);
    const[initialStyle,updatedStyle]=useState(
      {
        color:"white",
        fontSize:"1.6rem"

      }
    );
    const[initialStyleNav,updatedStyleNav]=useState(
      {
        backgroundColor:"transparent",
        height:"80px",
        zIndex:1
      }
    );
    const[initialStyleA,updatedStyleA]=useState(
      {
        color:"white",
        fontSize:"2.5rem"
      }
    );

            const [state, setState] = React.useState({
          checkedA: true
        });
      


        const handleChange = (event) => {
        setState({ ...state, [event.target.name]: event.target.checked });

        };           

        // state.checkedA==false?alert("hello"):alert('hi');        

            window.onscroll=()=>{
              if(window.scrollY>20){
                updatedStyle({...initialStyle,color:"white"})
                updatedStyleNav({...initialStyleNav,backgroundColor:"rgba(1,1,1,0.6)"})
                updatedStyleA({...initialStyle,color:"white"})
              }else{
                updatedStyleNav({...initialStyleNav,backgroundColor:"transparent"})
                updatedStyleA({...initialStyle,color:"white",fontSize:"2.5rem"})
              }
            }

    return(
        <>
         <nav className="navbar navbar-expand-md fixed-top navigation " style={initialStyleNav} >
  <a className="ml-md-5 navbar-brand linky"  href="#" style={initialStyleA}>Perfect Legal</a>
  <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#collapsibleNavbar">
    </button>
  <div className="collapse navbar-collapse" id="collapsibleNavbar">
    <ul className="navbar-nav ml-auto">
      <li className="nav-item mr-5">
        <Link  style={initialStyle} className="nav-link" to="/"> { cookies.jai&&Array.isArray(cookies.jai) ? cookies.jai.map((val)=>{return(<>{val}</>)})  : <>HOME</> }</Link>
      </li>
      <li className="nav-item mr-5">
        <Link  style={initialStyle} className="nav-link" to="/cookie">cookie </Link>
      </li>
      <li className="nav-item mr-5">
        <Link  style={initialStyle} className="nav-link" to="/csr">CSR</Link>
      </li> 
      <li className="nav-item dropdown mr-5">
      <Link  style={initialStyle} className="nav-link dropdown-toggle" to="#" id="navbardrop" data-toggle="dropdown">
      CONTACT US
      </Link>
      <div className="dropdown-menu bg-dark">
        <Link  style={initialStyle} className="nav-link" to="/local">Write To Us</Link>
      </div>
    </li>
    <li className="nav-item mr-5">
        <Link  style={initialStyle} className="nav-link" to="#">QUERIES</Link>
      </li>
      <li className="nav-item mr-5">
     {/* toggle */}
     <input type='checkbox' id='time'/>
<label for='time'>Night</label>
     {/* toggle end*/}     
      </li>    
    </ul>
  </div>  
</nav>




        </>
    );
}

export default Nav;
