import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import App from './App';
import {BrowserRouter} from "react-router-dom";
import {  LocalStoreProvider } from 'react-local-store';


ReactDOM.render(
  <LocalStoreProvider
    initialState={{
      title: 'react-local-store1'
    }}
    reducer={(state, action) => {
      switch (action.type) {
        case 'UPDATE_TITLE':
          return { ...state, title: action.payload1 };
        default:
          return state;
      }
    }}
  >
  <BrowserRouter>
    <App />
    </BrowserRouter>
  </LocalStoreProvider>

,
  document.getElementById('root')
);
