import React , {useState,useEffect} from 'react';


const Local=()=>{

    const[init,upda]=useState("black");


    const black=()=>{
        upda("black");
    }

    const white=()=>{
        upda("white");
        
    }

    
    localStorage.setItem('data',init);

    const va=localStorage.getItem('data');

    useEffect(() => {
        alert(va);
        if( va=="black" ){
            require('./dark.css')
        }else if(va=="white"){
            require('./light.css')
        }
      });

    return(
        <>
        <h1> localStorage color is {va} </h1>      
        <button onClick={black}>black</button>
        <button onClick={white}>white</button>
        </>
    );
}

export default Local;
