import React,{useEffect} from 'react';
import Nav from "./Nav";
import Side from "./SideNav";
import {Switch,Route} from "react-router-dom";
import Services from "./Services";
import Home from "./Home";
import About from "./About";
import Footer from "./Footer";
import Contact from "./Contact";
import ExtraDetails from "./ExtraDetails";
import MainService from "./MainService";
import PropertyMiniDetailes from "./PropertyMiniDetailes";
import Error from "./Error";
import PaymentInfo from "./PaymentInfo";
import { createMuiTheme, makeStyles, ThemeProvider } from '@material-ui/core/styles';
import {Provider} from "react-redux";
import store from "./redux/store"
import{darkMode} from "./redux"
import {connect} from "react-redux"


function App(props) {

    const theme = createMuiTheme({
      palette: {
        type:JSON.parse(localStorage.getItem('reduxStore')).mode,
      },
    });

    //  onClick={()=>props.darkMode("light")}
  //<h1>{JSON.parse(localStorage.getItem("reduxStore")).mode}</h1>

  return (  
    <>
  <ThemeProvider theme={theme}>
  {/* NAV */}


  <nav className="navbar navbar-expand-md fixed-top navigation " style={initialStyleNav} >
  <a className="ml-md-5 navbar-brand linky"  href="#" >
  <img src={logo} alt="logo" style={initialStyleA}/>
  </a>
  <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#collapsibleNavbar">
  <span class="navbar-toggler-icon" style={{position:'relative',right:"20px",zIndex:"4"}}>
    <MenuIcon style={{color:"white",fontSize:"25px"}}></MenuIcon>
  </span>
    </button>
  <div className="collapse navbar-collapse" id="collapsibleNavbar" >
    <ul className="navbar-nav ml-auto">
      <li className="nav-item mr-5">
        <Link  style={initialStyle} className="nav-link" to="/"> { cookies.jai&&Array.isArray(cookies.jai) ? cookies.jai.map((val)=>{return(<>{val}</>)})  : <>HOME</> }</Link>
      </li>
      <li className="nav-item mr-5">
        <Link  style={initialStyle} className="nav-link" to="/about">ABOUT</Link>
      </li>
      <li className="nav-item mr-5">
        <Link  style={initialStyle} className="nav-link" to="/services">SERVICES</Link>
      </li> 
      <li className="nav-item dropdown mr-5">
      <Link to="/contact" style={initialStyle} className="nav-link">
      CONTACT US
      </Link>
    </li>
    <li className="nav-item dropdown mr-5">
      <Link className="nav-link dropdown-toggle linky" id="navbardrop" style={{fontSize:"1.6rem"}} data-toggle="dropdown">
       PAYMENT
      </Link>
      <div className="dropdown-menu bg-dark">
        <Link className="dropdown-item dropdown" style={{fontSize:"1.5rem"}} to="#" >Direct Payment</Link>
        <Link className="dropdown-item dropdown" style={{fontSize:"1.5rem"}} to="paymentinfo">Payment Info.</Link>
      </div>
    </li>
      <li  className="nav-item mr-5 toggly">
     {/* toggle */}

     <div className="switch" style={{position: "relative" , top:'10px'}}>
	<input type="checkbox" name="toggle"/>
	<label for="toggle">
    <i className="bulb">
      <span className="bulb-center"></span>
      <span className="filament-1"></span>
      <span className="filament-2"></span>
      <span className="reflections">
        <span></span>
      </span>
      <span className="sparks">
        <i className="spark1"></i>
        <i className="spark2"></i>
        <i className="spark3"></i>
        <i className="spark4"></i>
      </span>
    </i>    
  </label>
</div>

     {/* toggle end*/}     
      </li>    
    </ul>
  </div>  
</nav>


<div>
            <div onClick={clicky} style={hid} >
<FontAwesomeIcon icon={faArrowUp} style={{fontSize:'3rem',color:"#fff",position:'relative',left:"50%",top:"50%",transform:"translate(-50%,-50%)"}}  />
             </div>
        </div>



  {/* NAV */}







    <Side/>
    <Switch>

    <Route exact path="/" component={Home} />
    <Route exact path="/about" component={About} />
    <Route exact path="/services" component={Services} />
    <Route exact path="/services/details" component={MainService} />
    <Route exact path="/services/details/:idd" component={PropertyMiniDetailes} />
    <Route exact path="/services/:id" component={ExtraDetails} />
    <Route exact path="/contact" component={Contact} />
    <Route exact path="/paymentinfo" component={PaymentInfo} />
    <Route exact path="*" component={Error} />

    </Switch>
<Footer/>
</ThemeProvider>
    </>
  );
}
const mapStatetoProps=(state)=>{

  return{
    mode:state.mode
  }

  }


  const mapDispatchToProps=(dispatch)=>{
  return{
    darkMode:function(light){ 
      dispatch(darkMode(light));
    }
  }
  }
export default  connect(mapStatetoProps,mapDispatchToProps) (App);
