import React from 'react';
import Grid from '@material-ui/core/Grid';
import PropTypes from 'prop-types';
import { makeStyles } from '@material-ui/core/styles';
import LinearProgress from '@material-ui/core/LinearProgress';
import Typography from '@material-ui/core/Typography';
import Box from '@material-ui/core/Box';
import Container from '@material-ui/core/Container';





function LinearProgressWithLabel(props) {
    return (
        <Box mr={1}   style={{height:"70px"}}>
    
          <Typography variant="h4"  style={{width:"50%",float:'left'}}>
          Success Cases
          </Typography>
                {/* ending and joining */}
          <Typography variant="h4" style={{width:"50%",float:'left'}}>{`${Math.round(
            props.value,
          )}%`}</Typography>
          <LinearProgress variant="determinate" style={{backgroundColor:"silver",height:"7px"}}  {...props} />
          
        </Box>
          
    );
  }



LinearProgressWithLabel.propTypes = {
    /**
     * The value of the progress indicator for the determinate and buffer variants.
     * Value between 0 and 100.
     */
    value: PropTypes.number.isRequired,
  };

  
const useStyles = makeStyles({
    root: {
      width: '100%',
    },
  });


export default function About() {


    const classes = useStyles();
    const [progress, setProgress] = React.useState(10);
    const [progress1, setProgress1] = React.useState(10);
    const [progress2, setProgress2] = React.useState(10);


    

            React.useEffect(()=>{
            const timer = setInterval(() => {
                setProgress((prevProgress) => (prevProgress >= 60 ? 60 : prevProgress + 10));
                setProgress1((prevProgress1) => (prevProgress1 >= 30 ? 30 : prevProgress1+ 10));
                setProgress2((prevProgress2) => (prevProgress2 >= 20 ? 20 : prevProgress2 + 10));
            }, 80);
            return () => {
                clearInterval(timer);
                clearInterval(timer);
              clearInterval(timer);
            };
        },[])



    return (
        <>
           <Grid container style={{zIndex:0}}>


            <Grid item xs={12} style={{height:"505px",backgroundImage:`url("https://attorco.themestek.com/demo1/wp-content/uploads/sites/4/revslider/home-slider-1/slider1-01.jpg")`,backgroundSize:"100% 505px",display:"flex",justifyContent:"center",alignItems:"center"}}>
                <div>
            <p className="top linky" style={{}}>Perfect Legal --- About Us</p>
            <h1 className="topu" style={{textAlign: "center"}}>ABOUT US</h1>
                </div>
            </Grid>

            <Grid style={{width: '100%', height:"525px",marginBottom:"60px",marginTop:"40px"}} item xs={12} container> 
                
                <Grid item xs={12} md={6}>
                <Container>
                    <div style={{width:'100%', height:'100%'}}>
                        <img src="https://attorco.themestek.com/demo1/wp-content/uploads/sites/4/2019/06/about-img.jpg" style={{width:'100%', height:'100%'}}></img>
                    </div>
                </Container>
                </Grid>


                <Grid item  xs={12} md={6} style={{paddingTop:'30px'}}>
                <Container>
                <p style={{color:"#CDBA6D",fontSize:"1.5rem"}} className="linky"><b>WHY CHOOSE US</b></p>
                    <h2 className="headi" style={{fontSize:'4rem'}}>
                        Experties In All Aspects<br></br> Of Divorce
                    </h2>
                    <p className="capital" style={{fontSize:"1.5rem"}}>Markets & Securities Far far away, behind the word mountains, far from the countries Vokalia and Consonantia, there live the blind texts.<br/><br/>
               A wonderful serenity has taken possession of my entire soul, like these sweet mornings of spring which I enjoy with my whole heart. I am alone, and feel the charm of existence in this spot,
               which was created for the bliss of souls like mine. With a genuine focus on client care. Grow organically the holistic world view.</p>

            {/* box1 */}

            <div className={classes.root} style={{marginTop:"30px"}}>
      <LinearProgressWithLabel value={progress}  />
    </div>

            {/* box1 */}


            {/* box2 */}

            <div className={classes.root}>
      <LinearProgressWithLabel value={progress1} />
    </div>

            {/* box2 */}


            {/* box3 */}

            <div className={classes.root}>
      <LinearProgressWithLabel value={progress2} />
    </div>

            {/* box3 */}

               </Container>
                </Grid>

            </Grid>
            

        

      </Grid>

        </>
    )
}
