import React , {useState} from 'react';
import { useCookies } from 'react-cookie';
import { useLocalStore } from 'react-local-store';



const Cookie=()=>{
    const [state, dispatch] = useLocalStore();
    const[init,upda]=useState(0);
    const [cookies, setCookie, removeCookie] = useCookies(['cookie-name']);

    const cooki=()=>{

        setCookie("jai", init )
        window.location.reload();
    }

    const recooki=()=>{
        window.location.reload();
        removeCookie("jai");
    }

    return(
        <>

        <button onClick={cooki}>GetCookie</button>
        <button onClick={recooki}>{state.title}</button>
        <button onClick={recooki}>{state.title}</button>
        </>
    );
}

export default Cookie;
