import React , {useEffect,useState} from 'react';
import Switch from '@material-ui/core/Switch';
import { useCookies } from 'react-cookie';
import {Link} from "react-router-dom";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faArrowUp } from '@fortawesome/free-solid-svg-icons'





const Nav=()=>{
    
    const [cookies, setCookie, removeCookie] = useCookies(['cookie-name']);
    const[hid,hidden]=useState({
      width:'40px',height:'40px',backgroundColor:'#CDBA6D',position:'fixed',bottom:"20px",right:'14px',borderRadius:'4px',boxShadow:"2px 2px 2px 1px #666666",zIndex:"10",cursor:"pointer",display:"none"
  });


    const[initialStyle,updatedStyle]=useState(
      {
        color:"white",
        fontSize:"1.6rem"

      }
    );
    const[initialStyleNav,updatedStyleNav]=useState(
      {
        backgroundColor:"transparent",
        height:"80px",
        zIndex:1
      }
    );
    const[initialStyleA,updatedStyleA]=useState(
      {
        color:"white",
        fontSize:"2.5rem"
      }
    );


            const [state, setState] = React.useState({
          checkedA: true
        });
      
  const clicky=()=>{
            document.body.scrollTop = 0;
            document.documentElement.scrollTop = 0;
        }


                 

        // state.checkedA==false?alert("hello"):alert('hi');        

        window.onscroll=()=>{
          if(window.scrollY>20){
                updatedStyle({...initialStyle,color:"white"})
                updatedStyleNav({...initialStyleNav,backgroundColor:"rgba(1,1,1,0.6)"})
                updatedStyleA({...initialStyle,color:"white"})
                hidden({...hid,display:"block"})
              }else{
                updatedStyleNav({...initialStyleNav,backgroundColor:"transparent"})
                hidden({...hid,display:"none"})
                updatedStyleA({...initialStyle,color:"white",fontSize:"2.5rem"})
              }
            }

    return(
        <>
         <nav className="navbar navbar-expand-md fixed-top navigation " style={initialStyleNav} >
  <a className="ml-md-5 navbar-brand linky"  href="#" style={initialStyleA}>Perfect Legal</a>
  <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#collapsibleNavbar">
  <span class="navbar-toggler-icon"></span>
    </button>
  <div className="collapse navbar-collapse" id="collapsibleNavbar">
    <ul className="navbar-nav ml-auto">
      <li className="nav-item mr-5">
        <Link  style={initialStyle} className="nav-link" to="/"> { cookies.jai&&Array.isArray(cookies.jai) ? cookies.jai.map((val)=>{return(<>{val}</>)})  : <>HOME</> }</Link>
      </li>
      <li className="nav-item mr-5">
        <Link  style={initialStyle} className="nav-link" to="/about">ABOUT</Link>
      </li>
      <li className="nav-item mr-5">
        <Link  style={initialStyle} className="nav-link" to="/services">SERVICES</Link>
      </li> 
      <li className="nav-item dropdown mr-5">
      <Link to="/contact" style={initialStyle} className="nav-link">
      CONTACT US
      </Link>
    </li>
    <li className="nav-item dropdown mr-5">
      <Link className="nav-link dropdown-toggle" id="navbardrop" data-toggle="dropdown">
       <strong>PAYMENT</strong>
      </Link>
      <div className="dropdown-menu bg-dark">
        <Link className="dropdown-item dropdown" style={{fontSize:"1.5rem"}} to="#" >Direct Payment</Link>
        <Link className="dropdown-item dropdown" style={{fontSize:"1.5rem"}} to="paymentinfo">Payment Info.</Link>
      </div>
    </li>
      <li className="nav-item mr-5">
     {/* toggle */}

     <div className="switch" style={{position: "relative" , top:'10px'}}>
	<input type="checkbox" name="toggle"/>
	<label for="toggle">
    <i className="bulb">
      <span className="bulb-center"></span>
      <span className="filament-1"></span>
      <span className="filament-2"></span>
      <span className="reflections">
        <span></span>
      </span>
      <span className="sparks">
        <i className="spark1"></i>
        <i className="spark2"></i>
        <i className="spark3"></i>
        <i className="spark4"></i>
      </span>
    </i>    
  </label>
</div>

     {/* toggle end*/}     
      </li>    
    </ul>
  </div>  
</nav>


<div>
            <div onClick={clicky} style={hid} >
<FontAwesomeIcon icon={faArrowUp} style={{fontSize:'27px',color:"#fff",margin:"8px 0 23px 10px"}}  />
             </div>
        </div>
        </>
    );
}

export default Nav;
